import React from "react";
import { motion } from "framer-motion";

const Sidebar = ({ activeView, onViewChange }) => {
  const menuItems = [
    {
      id: "notes",
      label: "Notes",
      icon: (
        <svg
          className="w-5 h-5"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"
          />
        </svg>
      ),
      color: "from-blue-500 to-blue-600",
      bgColor: "from-blue-50 to-blue-100",
      darkBgColor: "from-blue-900/20 to-blue-800/20",
    },
    {
      id: "kanban",
      label: "Kanban Board",
      icon: (
        <svg
          className="w-5 h-5"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M9 17V7m0 10a2 2 0 01-2 2H5a2 2 0 01-2-2V7a2 2 0 012-2h2a2 2 0 012 2m0 10a2 2 0 002 2h2a2 2 0 002-2M9 7a2 2 0 012-2h2a2 2 0 012 2m0 10V7m0 10a2 2 0 002 2h2a2 2 0 002-2V7a2 2 0 00-2-2H9a2 2 0 00-2 2"
          />
        </svg>
      ),
      color: "from-purple-500 to-purple-600",
      bgColor: "from-purple-50 to-purple-100",
      darkBgColor: "from-purple-900/20 to-purple-800/20",
    },
    {
      id: "todo",
      label: "Todo List",
      icon: (
        <svg
          className="w-5 h-5"
          fill="none"
          stroke="currentColor"
          viewBox="0 0 24 24"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            strokeWidth={2}
            d="M9 5H7a2 2 0 00-2 2v10a2 2 0 002 2h8a2 2 0 002-2V7a2 2 0 00-2-2h-2M9 5a2 2 0 002 2h2a2 2 0 002-2M9 5a2 2 0 012-2h2a2 2 0 012 2"
          />
        </svg>
      ),
      color: "from-green-500 to-green-600",
      bgColor: "from-green-50 to-green-100",
      darkBgColor: "from-green-900/20 to-green-800/20",
    },
  ];

  return (
    <motion.div
      initial={{ opacity: 0, x: -20 }}
      animate={{ opacity: 1, x: 0 }}
      transition={{ duration: 0.3 }}
      className="w-64 bg-gradient-to-b from-slate-900 to-slate-800 dark:from-slate-950 dark:to-slate-900 border-r border-slate-700 dark:border-slate-800 shadow-xl"
    >
      {/* Header */}
      <div className="p-6 border-b border-slate-700 dark:border-slate-800">
        <div className="flex items-center gap-3">
          <div className="w-10 h-10 bg-gradient-to-br from-blue-500 to-purple-600 rounded-xl flex items-center justify-center shadow-lg">
            <svg
              className="w-6 h-6 text-white"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"
              />
            </svg>
          </div>
          <div>
            <h1 className="text-xl font-bold bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
              MyNote
            </h1>
            <p className="text-xs text-slate-400">Productivity Hub</p>
          </div>
        </div>
      </div>

      {/* Navigation */}
      <nav className="p-4">
        <div className="space-y-2">
          {menuItems.map((item, index) => (
            <motion.button
              key={item.id}
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.3, delay: index * 0.1 }}
              onClick={() => onViewChange(item.id)}
              className={`w-full group relative overflow-hidden rounded-xl p-4 text-left transition-all duration-300 ${
                activeView === item.id
                  ? `bg-gradient-to-r ${item.bgColor} dark:${item.darkBgColor} border-2 border-slate-300 dark:border-slate-600 shadow-lg scale-[1.02]`
                  : "hover:bg-slate-800 dark:hover:bg-slate-800/50 hover:scale-[1.02] border-2 border-transparent"
              }`}
            >
              {/* Active indicator */}
              {activeView === item.id && (
                <motion.div
                  layoutId="activeIndicator"
                  className="absolute -left-1 top-1/2 -translate-y-1/2 w-2 h-8 bg-gradient-to-b from-blue-500 to-purple-500 rounded-r-full shadow-sm"
                  transition={{ type: "spring", stiffness: 300, damping: 30 }}
                />
              )}

              <div className="flex items-center gap-3">
                <div
                  className={`w-10 h-10 rounded-xl flex items-center justify-center transition-all duration-300 ${
                    activeView === item.id
                      ? `bg-gradient-to-br ${item.color} text-white shadow-lg`
                      : "bg-slate-700 dark:bg-slate-800 text-slate-400 group-hover:bg-slate-600 dark:group-hover:bg-slate-700 group-hover:text-slate-300"
                  }`}
                >
                  {item.icon}
                </div>
                <div className="flex-1">
                  <span
                    className={`font-medium transition-all duration-300 ${
                      activeView === item.id
                        ? "text-slate-900 dark:text-slate-100"
                        : "text-slate-300 group-hover:text-slate-100"
                    }`}
                  >
                    {item.label}
                  </span>
                  <p
                    className={`text-xs transition-all duration-300 ${
                      activeView === item.id
                        ? "text-slate-700 dark:text-slate-300"
                        : "text-slate-500 group-hover:text-slate-400"
                    }`}
                  >
                    {item.id === "notes" && "Organize your thoughts"}
                    {item.id === "kanban" && "Visual workflow management"}
                    {item.id === "todo" && "Track your progress"}
                  </p>
                </div>
              </div>

              {/* Hover effect */}
              <div
                className={`absolute inset-0 bg-gradient-to-r ${item.color} opacity-0 group-hover:opacity-5 transition-opacity duration-300 rounded-xl`}
              />
            </motion.button>
          ))}
        </div>
      </nav>

      {/* Footer */}
      <div className="absolute bottom-0 left-0 right-0 p-4 border-t border-slate-700 dark:border-slate-800">
        <div className="text-center">
          <div className="w-8 h-8 bg-gradient-to-br from-slate-600 to-slate-700 rounded-lg flex items-center justify-center mx-auto mb-2">
            <svg
              className="w-4 h-4 text-slate-300"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M13 16h-1v-4h-1m1-4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
              />
            </svg>
          </div>
          <p className="text-xs text-slate-400">v1.0.0</p>
          <p className="text-xs text-slate-500">Built with ❤️</p>
        </div>
      </div>
    </motion.div>
  );
};

export default Sidebar;
