import React from "react";

function NoteList({ notes, activeId, onSelect, onDelete }) {
  return (
    <div className="p-2">
      {notes.length === 0 && (
        <div className="text-center py-8">
          <div className="w-12 h-12 bg-slate-100 dark:bg-slate-700 rounded-full flex items-center justify-center mx-auto mb-3">
            <svg
              className="w-6 h-6 text-slate-400"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z"
              />
            </svg>
          </div>
          <p className="text-sm text-slate-500 dark:text-slate-400 mb-1">
            No notes found
          </p>
          <p className="text-xs text-slate-400 dark:text-slate-500">
            Try adjusting your search or filters
          </p>
        </div>
      )}
      <div className="space-y-2">
        {notes.map((n) => (
          <div
            key={n.id}
            className={`group p-3 rounded-lg cursor-pointer transition-all ${
              activeId === n.id
                ? "bg-blue-50 dark:bg-blue-950 border border-blue-200 dark:border-blue-800"
                : "hover:bg-slate-50 dark:hover:bg-gray-50 border"
            }`}
            onClick={() => onSelect(n.id)}
          >
            <div className="flex items-start gap-2">
              <div className="flex-1 min-w-0">
                <div className="flex items-center gap-2">
                  <h3 className="font-medium truncate text-slate-900 dark:text-slate-100">
                    {n.title || "Untitled"}
                  </h3>
                  <span className="text-[10px] text-slate-500 dark:text-slate-400">
                    {new Date(n.updatedAt).toLocaleString()}
                  </span>
                </div>
                <p className="text-xs text-slate-500 dark:text-slate-400 line-clamp-2">
                  {n.content || ""}
                </p>
                <div className="mt-1 flex flex-wrap gap-1">
                  {(n.tags || []).map((t) => (
                    <span
                      key={t}
                      className="text-[10px] px-1.5 py-0.5 rounded-full border border-slate-200 dark:border-slate-800 text-slate-600 dark:text-slate-400"
                    >
                      #{t}
                    </span>
                  ))}
                </div>
              </div>
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  onDelete(n.id);
                }}
                className="text-xs text-red-600 hover:text-red-800 dark:text-red-400 dark:hover:text-red-300 opacity-0 group-hover:opacity-100 transition-opacity"
              >
                <svg
                  className="w-4 h-4"
                  fill="none"
                  stroke="currentColor"
                  viewBox="0 0 24 24"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16"
                  />
                </svg>
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default NoteList;
