// Components
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import KanbanBoard from "./components/KanbanBoard";

function App() {
  const [activeView, setActiveView] = useState('feedback');
  const [feedbackItems, setFeedbackItems] = useState([
    {
      id: 12,
      content: "Maecenas sed diam eget risus varius blandit sit amet non magna. Cum sociis natoque penatibus et.",
      author: "Kevin Dukkon",
      timeAgo: "16 hours ago",
      priority: "medium",
      status: "todo"
    },
    {
      id: 34,
      content: "Cras mattis consectetur purus sit amet fermentum. Donec sed odio dui. Integer posuere erat a ante.",
      author: "Kevin Dukkon",
      timeAgo: "16 hours ago",
      priority: "medium",
      status: "todo"
    },
    {
      id: 87,
      content: "Aenean eu leo quam. Pellentesque ornare sem lacinia quam venenatis vestibulum. Donec ullamcorper nulla non metus.",
      author: "Kevin Dukkon",
      timeAgo: "16 hours ago",
      priority: "high",
      status: "todo"
    },
    {
      id: 4,
      content: "Aenean eu leo quam. Pellentesque ornare sem lacinia quam venenatis vestibulum. Donec ullamcorper nulla?",
      author: "Kevin Dukkon",
      timeAgo: "16 hours ago",
      priority: "high",
      status: "in-progress"
    },
    {
      id: 5,
      content: "Cras mattis consectetur purus sit amet fermentum. Donec sed odio dui. Integer posuere erat a ante.",
      author: "Kevin Dukkon",
      timeAgo: "16 hours ago",
      priority: "medium",
      status: "in-progress"
    },
    {
      id: 6,
      content: "Not sure if this really makes sense. Should we invest some time?",
      author: "Kevin Dukkon",
      timeAgo: "16 hours ago",
      priority: "high",
      status: "done"
    },
    {
      id: 12,
      content: "Cras mattis consectetur purus sit amet fermentum.",
      author: "Kevin Dukkon",
      timeAgo: "16 hours ago",
      priority: "low",
      status: "done"
    },
    {
      id: 9,
      content: "Aenean eu leo quam. Pellentesque ornare sem lacinia.",
      author: "Kevin Dukkon",
      timeAgo: "16 hours ago",
      priority: "low",
      status: "done"
    },
    {
      id: 78,
      content: "Maecenas sed diam eget risus varius blandit sit amet non magna.",
      author: "Kevin Dukkon",
      timeAgo: "16 hours ago",
      priority: "high",
      status: "done"
    }
  ]);

  const updateFeedbackItem = (id, updatedItem) => {
    setFeedbackItems(prev => 
      prev.map(item => 
        item.id === id ? updatedItem : item
      )
    );
  };

  return (
    <div className="h-screen flex bg-gray-50">
      {/* Sidebar */}
      <Sidebar activeView={activeView} onViewChange={setActiveView} />

      {/* Main content area */}
      <div className="flex-1 flex flex-col">
        {/* Header */}
        <Header />

        {/* Content */}
        <div className="flex-1 overflow-hidden">
          {activeView === 'feedback' && (
            <KanbanBoard 
              items={feedbackItems} 
              onUpdateItem={updateFeedbackItem}
            />
          )}
          {activeView === 'files' && (
            <div className="flex-1 p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4">Files</h2>
              <p className="text-gray-500">Files view coming soon...</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
