import React, { useEffect, useState, useRef } from "react";

function NoteEditor({ note, onChange, onUpdateTags, onUpdateStatus }) {
  const [title, setTitle] = useState(note.title);
  const [content, setContent] = useState(note.content);
  const [tagInput, setTagInput] = useState("");
  const isInitialMount = useRef(true);

  useEffect(() => {
    setTitle(note.title);
    setContent(note.content);
  }, [note.id]);

  useEffect(() => {
    if (isInitialMount.current) {
      isInitialMount.current = false;
      return;
    }
    const next = { ...note, title, content };
    onChange(next);
  }, [title, content]);

  const addTag = () => {
    const t = tagInput.trim();
    if (!t) return;
    const nextTags = Array.from(new Set([...(note.tags || []), t]));
    onUpdateTags(nextTags);
    setTagInput("");
  };

  const removeTag = (t) => {
    const nextTags = (note.tags || []).filter((x) => x !== t);
    onUpdateTags(nextTags);
  };

  const statusOptions = [
    {
      value: "todo",
      label: "To Do",
      color: "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200",
    },
    {
      value: "in-progress",
      label: "In Progress",
      color:
        "bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200",
    },
    {
      value: "done",
      label: "Done",
      color:
        "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200",
    },
  ];

  return (
    <div className="rounded-lg border border-slate-200 dark:border-slate-800 p-3 md:p-4">
      <input
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Title"
        className="w-full bg-transparent text-lg md:text-xl font-semibold outline-none placeholder:text-slate-400 text-slate-900 dark:text-slate-100"
      />
      {onUpdateStatus && (
        <div className="mt-2 flex items-center gap-2">
          <label className="text-xs text-slate-600 dark:text-slate-400">
            Status:
          </label>
          <select
            value={note.status || "todo"}
            onChange={(e) => onUpdateStatus(note.id, e.target.value)}
            className="text-xs px-2 py-1 rounded-md border border-slate-200 dark:border-slate-800 bg-white dark:bg-slate-950 focus:outline-none focus:ring-2 focus:ring-slate-400"
          >
            {statusOptions.map((option) => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        </div>
      )}
      <div className="mt-2 flex items-center gap-2">
        <input
          value={tagInput}
          onChange={(e) => setTagInput(e.target.value)}
          placeholder="Add tag and press Enter"
          onKeyDown={(e) => {
            if (e.key === "Enter") {
              e.preventDefault();
              addTag();
            }
          }}
          className="flex-1 rounded-md border border-slate-200 dark:border-slate-800 bg-white dark:bg-slate-950 px-2 py-1 text-xs focus:outline-none focus:ring-2 focus:ring-slate-400 text-slate-900 dark:text-slate-100 placeholder:text-slate-400"
        />
        <button
          onClick={addTag}
          className="text-xs px-2 py-1 rounded-md border border-slate-200 dark:border-slate-800 hover:bg-slate-50 dark:hover:bg-slate-900 text-slate-900 dark:text-slate-100"
        >
          Add
        </button>
      </div>
      <div className="mt-2 flex flex-wrap gap-1">
        {(note.tags || []).map((t) => (
          <span
            key={t}
            className="text-[10px] px-1.5 py-0.5 rounded-full border border-slate-200 dark:border-slate-800 text-slate-600 dark:text-slate-400"
          >
            #{t}
            <button
              onClick={() => removeTag(t)}
              className="ml-1 text-[10px] text-slate-500 hover:underline dark:text-slate-400 dark:hover:text-slate-300"
            >
              x
            </button>
          </span>
        ))}
      </div>
      <textarea
        value={content}
        onChange={(e) => setContent(e.target.value)}
        placeholder="Start typing..."
        className="mt-3 w-full min-h-[50vh] resize-none bg-transparent outline-none text-sm md:text-base leading-6 text-slate-900 dark:text-slate-100 placeholder:text-slate-400"
      />
    </div>
  );
}

export default NoteEditor;
