import React, { useEffect, useMemo, useState } from "react";
import { AnimatePresence, motion } from "framer-motion";

// Components
import SearchBar from "./components/SearchBar";
import TagFilter from "./components/TagFilter";
import NoteEditor from "./components/NoteEditor";
import ThemeToggle from "./components/ThemeToggle";

// Utils
import { loadNotes, saveNotes } from "./utils/storage";
import { importFromJson } from "./utils/io";

function App() {
  const [notes, setNotes] = useState(() => loadNotes());
  const [activeId, setActiveId] = useState(null);
  const [query, setQuery] = useState("");
  const [activeTags, setActiveTags] = useState([]);
  const [showColorPicker, setShowColorPicker] = useState(false);

  const generateId = () => {
    try {
      if (
        typeof crypto !== "undefined" &&
        typeof crypto.randomUUID === "function"
      ) {
        return crypto.randomUUID();
      }
    } catch {}
    // Fallback for older browsers
    const randomPart = Math.random().toString(16).slice(2);
    return `${Date.now()}-${randomPart}`;
  };

  // Derived
  const allTags = useMemo(() => {
    const tagSet = new Set();
    notes.forEach((n) => (n.tags || []).forEach((t) => tagSet.add(t)));
    return Array.from(tagSet).sort((a, b) => a.localeCompare(b));
  }, [notes]);

  const filteredNotes = useMemo(() => {
    const q = query.trim().toLowerCase();
    return notes.filter((n) => {
      const matchesQuery =
        q.length === 0 ||
        n.title.toLowerCase().includes(q) ||
        n.content.toLowerCase().includes(q) ||
        (n.tags || []).some((t) => t.toLowerCase().includes(q));
      const matchesTags =
        activeTags.length === 0 ||
        activeTags.every((t) => (n.tags || []).includes(t));
      return matchesQuery && matchesTags;
    });
  }, [notes, query, activeTags]);

  // Autosave
  useEffect(() => {
    saveNotes(notes);
  }, [notes]);

  const createNote = (colorClass) => {
    const newNote = {
      id: generateId(),
      title: "Untitled",
      content: "",
      tags: [],
      colorClass: colorClass || null,
      updatedAt: Date.now(),
    };
    setNotes((prev) => [newNote, ...prev]);
    setActiveId(newNote.id);
  };

  const updateNote = (id, updater) => {
    setNotes((prev) =>
      prev.map((n) =>
        n.id === id ? { ...updater(n), updatedAt: Date.now() } : n
      )
    );
  };

  const deleteNote = (id) => {
    setNotes((prev) => prev.filter((n) => n.id !== id));
    if (activeId === id) setActiveId(null);
  };

  const selected = useMemo(
    () => notes.find((n) => n.id === activeId) || null,
    [notes, activeId]
  );

  const [recentRange, setRecentRange] = useState("This Week");
  const [notesRange, setNotesRange] = useState("Todays");

  const TimeTabs = ({ value, onChange }) => {
    const options = ["Todays", "This Week", "This Month"];
    return (
      <div className="flex items-center gap-3 text-sm">
        {options.map((opt) => (
          <button
            key={opt}
            className={`pb-1 border-b-2 transition-colors ${
              value === opt
                ? "border-slate-900 dark:border-slate-100 text-slate-900 dark:text-slate-100"
                : "border-transparent text-slate-500 hover:text-slate-700 dark:hover:text-slate-300"
            }`}
            onClick={() => onChange(opt)}
          >
            {opt}
          </button>
        ))}
      </div>
    );
  };

  const cardBgClasses = [
    "bg-sky-400",
    "bg-rose-400",
    "bg-amber-400",
    "bg-blue-400",
  ];

  const NoteCard = ({ note, colorClass }) => (
    <div
      className={`relative text-left rounded-xl border border-slate-200 dark:border-slate-800 p-4 hover:shadow-sm transition-colors ${
        note.colorClass || colorClass
      } dark:bg-slate-800`}
      onClick={() => setActiveId(note.id)}
      role="button"
      tabIndex={0}
    >
      <div className="flex items-start justify-between">
        <h3 className="font-medium truncate mr-2">
          {note.title || "Untitled"}
        </h3>
        <span className="text-[10px] text-slate-600 dark:text-slate-400">
          {new Date(note.updatedAt).toLocaleDateString()}
        </span>
      </div>
      <p className="mt-1 text-sm text-slate-700 dark:text-slate-300 line-clamp-3">
        {note.content}
      </p>
      <div className="mt-2 flex flex-wrap gap-1">
        {(note.tags || []).map((t) => (
          <span
            key={t}
            className="text-[10px] px-1.5 py-0.5 rounded-full border border-slate-200 dark:border-slate-800"
          >
            #{t}
          </span>
        ))}
      </div>
      <button
        onClick={(e) => {
          e.stopPropagation();
          deleteNote(note.id);
        }}
        aria-label="Delete note"
        className="absolute top-12 right-2 h-7 w-7 grid place-items-center rounded-full  text-slate-700 shadow-sm dark:bg-slate-700/70 dark:hover:bg-white-300"
        title="Delete"
      >
        x
      </button>
    </div>
  );

  const recentTags = allTags.slice(0, 4);

  return (
    <div className="min-h-dvh bg-white text-slate-900 dark:bg-slate-950 dark:text-slate-100 transition-colors">
      <div className="mx-auto max-w-7xl px-4 md:px-6 py-4 md:py-6">
        {/* Header */}
        <div className="flex items-center gap-3">
          <h1 className="text-2xl md:text-3xl font-semibold tracking-tight">
            MY NOTES<span className="text-yellow-500">.</span>
          </h1>

          <div className="ml-auto flex items-center gap-2 relative">
            <div className="hidden md:block w-64">
              <SearchBar value={query} onChange={setQuery} />
            </div>
            <button
              onClick={() => setShowColorPicker((v) => !v)}
              className="rounded-md bg-slate-900 text-white dark:bg-slate-100 dark:text-slate-900 px-3 py-1.5 text-sm md:text-base hover:opacity-90 focus:outline-none focus:ring-2 focus:ring-slate-400"
            >
              New Note
            </button>
            <ThemeToggle />

            {showColorPicker && (
              <div className="absolute right-0 top-full mt-2 z-10 rounded-lg border border-slate-200 dark:border-slate-800 bg-white dark:bg-slate-900 p-3 shadow-lg">
                <p className="text-xs mb-2 text-slate-500">Choose color</p>
                <div className="grid grid-cols-4 gap-2">
                  {cardBgClasses.map((c) => (
                    <button
                      key={c}
                      className={`h-8 w-8 rounded-md border border-black/5 ${c}`}
                      onClick={() => {
                        createNote(c);
                        setShowColorPicker(false);
                      }}
                      aria-label={`Create note with ${c}`}
                    />
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>

        <div className="mt-6 grid grid-cols-12 gap-6 ">
          {/* Sidebar */}
          <aside className="hidden md:block col-span-3 lg:col-span-2">
            <div className="sticky top-4 space-y-4">
              {/* Left rail quick actions */}
              <div className="rounded-xl border border-slate-200 dark:border-slate-800 p-4">
                <p className="font-semibold tracking-tight">Add new</p>
                <div className="mt-3 flex items-center gap-2">
                  <span className="h-3 w-3 rounded-full bg-rose-500" />
                  <span className="h-3 w-3 rounded-full bg-amber-400" />
                  <span className="h-3 w-3 rounded-full bg-sky-500" />
                </div>
              </div>

              <nav className="rounded-xl border border-slate-200 dark:border-slate-800 p-2">
                {[
                  { label: "Calendar" },
                  { label: "Archive" },
                  { label: "Trash" },
                ].map((item) => (
                  <button
                    key={item.label}
                    className="w-full text-left px-3 py-2 rounded-md hover:bg-slate-50 dark:hover:bg-slate-900 text-sm"
                  >
                    {item.label}
                  </button>
                ))}
              </nav>

              <div className="rounded-xl border border-slate-200 dark:border-slate-800 p-3">
                <p className="text-xs uppercase text-slate-500 mb-2">Filters</p>
                <TagFilter
                  tags={allTags}
                  active={activeTags}
                  onToggle={(t) => {
                    setActiveTags((prev) =>
                      prev.includes(t)
                        ? prev.filter((x) => x !== t)
                        : [...prev, t]
                    );
                  }}
                />
              </div>

              <div className="flex flex-wrap gap-2">
                <label className="px-3 py-1.5 rounded-md border border-slate-200 dark:border-slate-800 text-sm hover:bg-slate-50 dark:hover:bg-slate-900 cursor-pointer">
                  Import JSON
                  <input
                    type="file"
                    accept="application/json"
                    className="hidden"
                    onChange={async (e) => {
                      const file = e.target.files?.[0];
                      if (!file) return;
                      const loaded = await importFromJson(file);
                      setNotes(loaded);
                      setActiveId(loaded[0]?.id ?? null);
                      e.target.value = "";
                    }}
                  />
                </label>
              </div>
            </div>
          </aside>

          {/* Main content */}
          <main className="col-span-12 md:col-span-9 lg:col-span-10">
            {/* My Notes */}
            <section className="mt-8">
              <div className="flex items-center justify-between mb-3">
                <h2 className="text-lg font-semibold">My Notes</h2>
                <TimeTabs value={notesRange} onChange={setNotesRange} />
              </div>
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
                {filteredNotes.map((n, i) => (
                  <NoteCard
                    key={n.id}
                    note={n}
                    colorClass={cardBgClasses[i % cardBgClasses.length]}
                  />
                ))}
                {/* New note placeholder */}
                <button
                  onClick={createNote}
                  className="rounded-xl border-2 border-dashed border-slate-300 dark:border-slate-700 p-4 text-left hover:bg-slate-50/60 dark:hover:bg-slate-900"
                >
                  <div className="h-5 w-10 rounded-lg bg-yellow-200 dark:bg-slate-800 mb-3" />
                  <div className="h-5 w-5 rounded-lg bg-yellow-200 dark:bg-slate-800 mb-3" />
                  <div className="h-5 w-5 rounded-lg bg-yellow-200 dark:bg-slate-800 mb-3" />

                  <p className="font-medium">New Note</p>
                </button>
              </div>
            </section>

            {/* Editor */}
            <section className="mt-8">
              <AnimatePresence mode="wait">
                {selected ? (
                  <motion.div
                    key={selected.id}
                    initial={{ opacity: 0, y: 8 }}
                    animate={{ opacity: 1, y: 0 }}
                    exit={{ opacity: 0, y: -8 }}
                    transition={{ duration: 0.15 }}
                  >
                    <NoteEditor
                      note={selected}
                      onChange={(next) => updateNote(selected.id, () => next)}
                      onUpdateTags={(tags) =>
                        updateNote(selected.id, (n) => ({ ...n, tags }))
                      }
                    />
                  </motion.div>
                ) : (
                  <div className="h-full min-h-[20vh] grid place-items-center border border-dashed border-slate-300 dark:border-slate-800 rounded-lg">
                    <p className="text-slate-500">
                      Select or create a note to edit
                    </p>
                  </div>
                )}
              </AnimatePresence>
            </section>
          </main>
        </div>
      </div>
    </div>
  );
}

export default App;
