import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

function TodoList({ notes, activeId, onSelect, onDelete, onUpdateStatus }) {
  const [filter, setFilter] = useState("all"); // all, completed, pending

  const filteredNotes = notes.filter((note) => {
    if (filter === "completed") return note.status === "done";
    if (filter === "pending") return note.status !== "done";
    return true;
  });

  const handleToggleStatus = (noteId, currentStatus) => {
    const newStatus = currentStatus === "done" ? "todo" : "done";
    onUpdateStatus(noteId, newStatus);
  };

  const completedCount = notes.filter((n) => n.status === "done").length;
  const pendingCount = notes.filter((n) => n.status !== "done").length;

  return (
    <div className="max-w-4xl mx-auto p-6">
      {/* Header */}
      <div className="mb-6">
        <h1 className="text-2xl font-bold text-slate-900 dark:text-slate-100 mb-2">
          Todo List
        </h1>
        <div className="flex items-center gap-4 text-sm text-slate-600 dark:text-slate-400">
          <span>{pendingCount} pending</span>
          <span>{completedCount} completed</span>
        </div>
      </div>

      {/* Filter Tabs */}
      <div className="flex gap-1 mb-6 p-1 bg-slate-100 dark:bg-slate-800 rounded-lg w-fit">
        {[
          { id: "all", label: "All", count: notes.length },
          { id: "pending", label: "Pending", count: pendingCount },
          { id: "completed", label: "Completed", count: completedCount },
        ].map((tab) => (
          <button
            key={tab.id}
            onClick={() => setFilter(tab.id)}
            className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
              filter === tab.id
                ? "bg-white dark:bg-slate-700 text-slate-900 dark:text-slate-100 shadow-sm"
                : "text-slate-600 dark:text-slate-400 hover:text-slate-900 dark:hover:text-slate-100"
            }`}
          >
            {tab.label} ({tab.count})
          </button>
        ))}
      </div>

      {/* Todo Items */}
      <div className="space-y-2">
        <AnimatePresence>
          {filteredNotes.map((note) => (
            <motion.div
              key={note.id}
              layout
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.2 }}
              className={`group flex items-start gap-3 p-4 rounded-lg border border-slate-200 dark:border-slate-700 bg-white dark:bg-slate-800 hover:shadow-sm transition-all ${
                activeId === note.id ? "ring-2 ring-blue-500" : ""
              } ${note.status === "done" ? "opacity-75" : ""}`}
            >
              {/* Checkbox */}
              <button
                onClick={() => handleToggleStatus(note.id, note.status)}
                className={`flex-shrink-0 w-5 h-5 rounded border-2 transition-colors ${
                  note.status === "done"
                    ? "bg-green-500 border-green-500 text-white"
                    : "border-slate-300 dark:border-slate-600 hover:border-green-500"
                }`}
              >
                {note.status === "done" && (
                  <svg
                    className="w-3 h-3"
                    fill="currentColor"
                    viewBox="0 0 20 20"
                  >
                    <path
                      fillRule="evenodd"
                      d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z"
                      clipRule="evenodd"
                    />
                  </svg>
                )}
              </button>

              {/* Content */}
              <div
                className="flex-1 min-w-0 cursor-pointer"
                onClick={() => onSelect(note.id)}
              >
                <div className="flex items-start justify-between gap-2">
                  <div className="flex-1 min-w-0">
                    <h3
                      className={`font-medium text-sm mb-1 ${
                        note.status === "done"
                          ? "line-through text-slate-500 dark:text-slate-400"
                          : "text-slate-900 dark:text-slate-100"
                      }`}
                    >
                      {note.title || "Untitled"}
                    </h3>
                    <p
                      className={`text-xs mb-2 line-clamp-2 ${
                        note.status === "done"
                          ? "text-slate-400 dark:text-slate-500"
                          : "text-slate-600 dark:text-slate-400"
                      }`}
                    >
                      {note.content || ""}
                    </p>
                    <div className="flex items-center gap-2">
                      <div className="flex flex-wrap gap-1">
                        {(note.tags || []).map((tag) => (
                          <span
                            key={tag}
                            className="text-[10px] px-1.5 py-0.5 rounded-full bg-slate-100 dark:bg-slate-700 text-slate-600 dark:text-slate-400"
                          >
                            #{tag}
                          </span>
                        ))}
                      </div>
                      <span className="text-[10px] text-slate-500">
                        {new Date(note.updatedAt).toLocaleDateString()}
                      </span>
                    </div>
                  </div>
                </div>
              </div>

              {/* Actions */}
              <div className="flex-shrink-0 opacity-0 group-hover:opacity-100 transition-opacity">
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    onDelete(note.id);
                  }}
                  className="text-slate-400 hover:text-red-500 dark:text-slate-500 dark:hover:text-red-400"
                >
                  <svg
                    className="w-4 h-4"
                    fill="none"
                    stroke="currentColor"
                    viewBox="0 0 24 24"
                  >
                    <path
                      strokeLinecap="round"
                      strokeLinejoin="round"
                      strokeWidth={2}
                      d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16"
                    />
                  </svg>
                </button>
              </div>
            </motion.div>
          ))}
        </AnimatePresence>

        {filteredNotes.length === 0 && (
          <div className="text-center py-8 text-slate-500 dark:text-slate-400">
            {filter === "all" && "No todos yet"}
            {filter === "pending" && "No pending todos"}
            {filter === "completed" && "No completed todos"}
          </div>
        )}
      </div>
    </div>
  );
}

export default TodoList;
