import React, { useEffect, useMemo, useState } from "react";
import { AnimatePresence, motion } from "framer-motion";

// Components
import SearchBar from "./components/SearchBar";
import TagFilter from "./components/TagFilter";
import NoteList from "./components/NoteList";
import NoteEditor from "./components/NoteEditor";
import ThemeToggle from "./components/ThemeToggle";
import ViewToggle from "./components/ViewToggle";
import KanbanView from "./components/KanbanView";
import Sidebar from "./components/Sidebar";
import TodoList from "./components/TodoList";

// Utils
import { loadNotes, saveNotes } from "./utils/storage";
import { exportAsJson, exportAsMarkdown, importFromJson } from "./utils/io";

function App() {
  const [notes, setNotes] = useState(() => loadNotes());
  const [activeId, setActiveId] = useState(null);
  const [query, setQuery] = useState("");
  const [activeTags, setActiveTags] = useState([]);
  const [view, setView] = useState("list");
  const [activeView, setActiveView] = useState("notes");

  // Derived
  const allTags = useMemo(() => {
    const tagSet = new Set();
    notes.forEach((n) => (n.tags || []).forEach((t) => tagSet.add(t)));
    return Array.from(tagSet).sort((a, b) => a.localeCompare(b));
  }, [notes]);

  const filteredNotes = useMemo(() => {
    const q = query.trim().toLowerCase();
    return notes.filter((n) => {
      const matchesQuery =
        q.length === 0 ||
        n.title.toLowerCase().includes(q) ||
        n.content.toLowerCase().includes(q) ||
        (n.tags || []).some((t) => t.toLowerCase().includes(q));
      const matchesTags =
        activeTags.length === 0 ||
        activeTags.every((t) => (n.tags || []).includes(t));
      return matchesQuery && matchesTags;
    });
  }, [notes, query, activeTags]);

  // Autosave
  useEffect(() => {
    saveNotes(notes);
  }, [notes]);

  const createNote = () => {
    const newNote = {
      id: crypto.randomUUID(),
      title: "Untitled",
      content: "",
      tags: [],
      status: "todo",
      updatedAt: Date.now(),
    };
    setNotes((prev) => [newNote, ...prev]);
    setActiveId(newNote.id);
  };

  const updateNote = (id, updater) => {
    setNotes((prev) =>
      prev.map((n) =>
        n.id === id ? { ...updater(n), updatedAt: Date.now() } : n
      )
    );
  };

  const deleteNote = (id) => {
    setNotes((prev) => prev.filter((n) => n.id !== id));
    if (activeId === id) setActiveId(null);
  };

  const updateNoteStatus = (id, status) => {
    setNotes((prev) =>
      prev.map((n) =>
        n.id === id ? { ...n, status, updatedAt: Date.now() } : n
      )
    );
  };

  const selected = useMemo(
    () => notes.find((n) => n.id === activeId) || null,
    [notes, activeId]
  );

  return (
    <div className="min-h-dvh bg-white text-slate-900 dark:bg-slate-950 dark:text-slate-100 transition-colors">
      <div className="flex h-screen">
        {/* Sidebar */}
        <Sidebar activeView={activeView} onViewChange={setActiveView} />

        {/* Main Content */}
        <div className="flex-1 flex flex-col overflow-hidden">
          {/* Top Header */}
          <header className="bg-white dark:bg-slate-900 border-b border-slate-200 dark:border-slate-800 px-6 py-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-4">
                <h1 className="text-xl font-semibold">
                  {activeView === "notes" && "Notes"}
                  {activeView === "kanban" && "Kanban Board"}
                  {activeView === "todo" && "Todo List"}
                </h1>
              </div>
              <div className="flex items-center gap-2">
                <button
                  onClick={createNote}
                  className="rounded-md bg-slate-900 text-white dark:bg-slate-100 dark:text-slate-900 px-3 py-1.5 text-sm hover:opacity-90 focus:outline-none focus:ring-2 focus:ring-slate-400"
                >
                  New Note
                </button>
                <ThemeToggle />
              </div>
            </div>
          </header>

          {/* Content Area */}
          <main className="flex-1 overflow-hidden">
            {activeView === "notes" && (
              <div className="h-full flex">
                {/* Notes Sidebar */}
                <div className="w-80 border-r border-slate-200 dark:border-slate-800 flex flex-col">
                  <div className="p-4 border-b border-slate-200 dark:border-slate-800">
                    <div className="flex items-center justify-between mb-4">
                      <h2 className="text-lg font-medium">Notes</h2>
                      <ViewToggle view={view} onViewChange={setView} />
                    </div>
                    <SearchBar value={query} onChange={setQuery} />
                  </div>

                  <div className="p-4 border-b border-slate-200 dark:border-slate-800">
                    <TagFilter
                      tags={allTags}
                      active={activeTags}
                      onToggle={(t) => {
                        setActiveTags((prev) =>
                          prev.includes(t) ? prev.filter((x) => x !== t) : [...prev, t]
                        );
                      }}
                    />
                  </div>

                  <div className="flex-1 overflow-y-auto">
                    {view === "list" ? (
                      <NoteList
                        notes={filteredNotes}
                        activeId={activeId}
                        onSelect={setActiveId}
                        onDelete={deleteNote}
                      />
                    ) : (
                      <KanbanView
                        notes={filteredNotes}
                        activeId={activeId}
                        onSelect={setActiveId}
                        onDelete={deleteNote}
                        onUpdateStatus={updateNoteStatus}
                      />
                    )}
                  </div>

                  <div className="p-4 border-t border-slate-200 dark:border-slate-800">
                    <div className="flex flex-wrap gap-2">
                      <button
                        onClick={() => exportAsJson(notes)}
                        className="px-3 py-1.5 rounded-md border border-slate-200 dark:border-slate-800 text-sm hover:bg-slate-50 dark:hover:bg-slate-900"
                      >
                        Export JSON
                      </button>
                      <button
                        onClick={() => exportAsMarkdown(notes)}
                        className="px-3 py-1.5 rounded-md border border-slate-200 dark:border-slate-800 text-sm hover:bg-slate-50 dark:hover:bg-slate-900"
                      >
                        Export MD
                      </button>
                      <label className="px-3 py-1.5 rounded-md border border-slate-200 dark:border-slate-800 text-sm hover:bg-slate-50 dark:hover:bg-slate-900 cursor-pointer">
                        Import JSON
                        <input
                          type="file"
                          accept="application/json"
                          className="hidden"
                          onChange={async (e) => {
                            const file = e.target.files?.[0];
                            if (!file) return;
                            const loaded = await importFromJson(file);
                            setNotes(loaded);
                            setActiveId(loaded[0]?.id ?? null);
                            e.target.value = "";
                          }}
                        />
                      </label>
                    </div>
                  </div>
                </div>

                {/* Note Editor */}
                <div className="flex-1">
                  <AnimatePresence mode="wait">
                    {selected ? (
                      <motion.div
                        key={selected.id}
                        initial={{ opacity: 0, x: 20 }}
                        animate={{ opacity: 1, x: 0 }}
                        exit={{ opacity: 0, x: -20 }}
                        transition={{ duration: 0.15 }}
                        className="h-full"
                      >
                        <NoteEditor
                          note={selected}
                          onChange={(next) => updateNote(selected.id, () => next)}
                          onUpdateTags={(tags) =>
                            updateNote(selected.id, (n) => ({ ...n, tags }))
                          }
                          onUpdateStatus={updateNoteStatus}
                        />
                      </motion.div>
                    ) : (
                      <div className="h-full grid place-items-center border border-dashed border-slate-300 dark:border-slate-800">
                        <p className="text-slate-500">
                          Select or create a note to get started
                        </p>
                      </div>
                    )}
                  </AnimatePresence>
                </div>
              </div>
            )}

            {activeView === "kanban" && (
              <div className="h-full overflow-y-auto">
                <KanbanView
                  notes={filteredNotes}
                  activeId={activeId}
                  onSelect={setActiveId}
                  onDelete={deleteNote}
                  onUpdateStatus={updateNoteStatus}
                />

                {/* Modal for editing notes in Kanban view */}
                {selected && (
                  <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
                    <div className="bg-white dark:bg-slate-900 rounded-lg shadow-xl max-w-4xl w-full max-h-[90vh] overflow-hidden">
                      <div className="flex items-center justify-between p-4 border-b border-slate-200 dark:border-slate-700">
                        <h3 className="text-lg font-medium">Edit Note</h3>
                        <button
                          onClick={() => setActiveId(null)}
                          className="text-slate-500 hover:text-slate-700 dark:text-slate-400 dark:hover:text-slate-200"
                        >
                          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                          </svg>
                        </button>
                      </div>
                      <div className="p-4 overflow-y-auto max-h-[calc(90vh-80px)]">
                        <NoteEditor
                          note={selected}
                          onChange={(next) => updateNote(selected.id, () => next)}
                          onUpdateTags={(tags) =>
                            updateNote(selected.id, (n) => ({ ...n, tags }))
                          }
                          onUpdateStatus={updateNoteStatus}
                        />
                      </div>
                    </div>
                  </div>
                )}
              </div>
            )}

            {activeView === "todo" && (
              <div className="h-full overflow-y-auto">
                <TodoList
                  notes={filteredNotes}
                  activeId={activeId}
                  onSelect={setActiveId}
                  onDelete={deleteNote}
                  onUpdateStatus={updateNoteStatus}
                />

                {/* Modal for editing todos */}
                {selected && (
                  <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
                    <div className="bg-white dark:bg-slate-900 rounded-lg shadow-xl max-w-4xl w-full max-h-[90vh] overflow-hidden">
                      <div className="flex items-center justify-between p-4 border-b border-slate-200 dark:border-slate-700">
                        <h3 className="text-lg font-medium">Edit Todo</h3>
                        <button
                          onClick={() => setActiveId(null)}
                          className="text-slate-500 hover:text-slate-700 dark:text-slate-400 dark:hover:text-slate-200"
                        >
                          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                          </svg>
                        </button>
                      </div>
                      <div className="p-4 overflow-y-auto max-h-[calc(90vh-80px)]">
                        <NoteEditor
                          note={selected}
                          onChange={(next) => updateNote(selected.id, () => next)}
                          onUpdateTags={(tags) =>
                            updateNote(selected.id, (n) => ({ ...n, tags }))
                          }
                          onUpdateStatus={updateNoteStatus}
                        />
                      </div>
                    </div>
                  </div>
                )}
              </div>
            )}
          </main>
        </div>
      </div>
    </div>
  );
}

export default App;
