import React, { useEffect, useMemo, useState } from "react";
import { AnimatePresence, motion } from "framer-motion";

// Components
import Sidebar from "./components/Sidebar";
import Header from "./components/Header";
import KanbanBoard from "./components/KanbanBoard";
import NoteEditor from "./components/NoteEditor";
import SearchBar from "./components/SearchBar";
import TagFilter from "./components/TagFilter";
import NoteList from "./components/NoteList";
import ThemeToggle from "./components/ThemeToggle";

// Utils
import { loadNotes, saveNotes } from "./utils/storage";
import { exportAsJson, exportAsMarkdown, importFromJson } from "./utils/io";

function App() {
  const [notes, setNotes] = useState(() => loadNotes());
  const [activeId, setActiveId] = useState(null);
  const [query, setQuery] = useState("");
  const [activeTags, setActiveTags] = useState([]);
  const [activeView, setActiveView] = useState('notes');

  // Derived
  const allTags = useMemo(() => {
    const tagSet = new Set();
    notes.forEach((n) => (n.tags || []).forEach((t) => tagSet.add(t)));
    return Array.from(tagSet).sort((a, b) => a.localeCompare(b));
  }, [notes]);

  const filteredNotes = useMemo(() => {
    const q = query.trim().toLowerCase();
    return notes.filter((n) => {
      const matchesQuery =
        q.length === 0 ||
        n.title.toLowerCase().includes(q) ||
        n.content.toLowerCase().includes(q) ||
        (n.tags || []).some((t) => t.toLowerCase().includes(q));
      const matchesTags =
        activeTags.length === 0 ||
        activeTags.every((t) => (n.tags || []).includes(t));
      return matchesQuery && matchesTags;
    });
  }, [notes, query, activeTags]);

  // Autosave
  useEffect(() => {
    saveNotes(notes);
  }, [notes]);

  const createNote = () => {
    const newNote = {
      id: crypto.randomUUID(),
      title: "Untitled",
      content: "",
      tags: [],
      status: "todo", // Add status for kanban view
      updatedAt: Date.now(),
    };
    setNotes((prev) => [newNote, ...prev]);
    setActiveId(newNote.id);
  };

  const updateNote = (id, updater) => {
    setNotes((prev) =>
      prev.map((n) =>
        n.id === id ? { ...updater(n), updatedAt: Date.now() } : n
      )
    );
  };

  const deleteNote = (id) => {
    setNotes((prev) => prev.filter((n) => n.id !== id));
    if (activeId === id) setActiveId(null);
  };

  const selected = useMemo(
    () => notes.find((n) => n.id === activeId) || null,
    [notes, activeId]
  );

  // Convert notes to kanban items
  const kanbanItems = useMemo(() => {
    return notes.map(note => ({
      id: note.id,
      content: note.content || note.title,
      author: "You",
      timeAgo: "Just now",
      priority: note.tags?.includes('high') ? 'high' : note.tags?.includes('low') ? 'low' : 'medium',
      status: note.status || 'todo',
      note: note // Keep reference to original note
    }));
  }, [notes]);

  const updateKanbanItem = (id, updatedItem) => {
    const note = notes.find(n => n.id === id);
    if (note) {
      updateNote(id, () => ({ ...note, status: updatedItem.status }));
    }
  };

  return (
    <div className="h-screen flex bg-gray-50">
      {/* Sidebar */}
      <Sidebar activeView={activeView} onViewChange={setActiveView} />

      {/* Main content area */}
      <div className="flex-1 flex flex-col">
        {/* Header */}
        <Header />

        {/* Content */}
        <div className="flex-1 overflow-hidden">
          {activeView === 'notes' && (
            <div className="flex h-full">
              {/* Left panel - Search, filters, and note list */}
              <div className="w-80 bg-white border-r border-gray-200 flex flex-col">
                <div className="p-4 border-b border-gray-200">
                  <div className="flex items-center justify-between mb-4">
                    <h2 className="text-lg font-semibold text-gray-900">My Notes</h2>
                    <ThemeToggle />
                  </div>
                  <button
                    onClick={createNote}
                    className="w-full bg-blue-600 text-white rounded-lg px-4 py-2 text-sm font-medium hover:bg-blue-700 transition-colors"
                  >
                    + New Note
                  </button>
                </div>

                <div className="flex-1 flex flex-col p-4 space-y-4">
                  <SearchBar value={query} onChange={setQuery} />
                  <TagFilter
                    tags={allTags}
                    active={activeTags}
                    onToggle={(t) => {
                      setActiveTags((prev) =>
                        prev.includes(t) ? prev.filter((x) => x !== t) : [...prev, t]
                      );
                    }}
                  />
                  <div className="flex-1 overflow-hidden">
                    <NoteList
                      notes={filteredNotes}
                      activeId={activeId}
                      onSelect={setActiveId}
                      onDelete={deleteNote}
                    />
                  </div>

                  <div className="flex flex-wrap gap-2 pt-4 border-t border-gray-200">
                    <button
                      onClick={() => exportAsJson(notes)}
                      className="px-3 py-1.5 rounded-md border border-gray-200 text-sm hover:bg-gray-50"
                    >
                      Export JSON
                    </button>
                    <button
                      onClick={() => exportAsMarkdown(notes)}
                      className="px-3 py-1.5 rounded-md border border-gray-200 text-sm hover:bg-gray-50"
                    >
                      Export MD
                    </button>
                    <label className="px-3 py-1.5 rounded-md border border-gray-200 text-sm hover:bg-gray-50 cursor-pointer">
                      Import JSON
                      <input
                        type="file"
                        accept="application/json"
                        className="hidden"
                        onChange={async (e) => {
                          const file = e.target.files?.[0];
                          if (!file) return;
                          const loaded = await importFromJson(file);
                          setNotes(loaded);
                          setActiveId(loaded[0]?.id ?? null);
                          e.target.value = "";
                        }}
                      />
                    </label>
                  </div>
                </div>
              </div>

              {/* Right panel - Note editor or Kanban view */}
              <div className="flex-1 flex flex-col">
                {selected ? (
                  <AnimatePresence mode="wait">
                    <motion.div
                      key={selected.id}
                      initial={{ opacity: 0, y: 8 }}
                      animate={{ opacity: 1, y: 0 }}
                      exit={{ opacity: 0, y: -8 }}
                      transition={{ duration: 0.15 }}
                      className="flex-1 p-6"
                    >
                      <NoteEditor
                        note={selected}
                        onChange={(next) => updateNote(selected.id, () => next)}
                        onUpdateTags={(tags) =>
                          updateNote(selected.id, (n) => ({ ...n, tags }))
                        }
                      />
                    </motion.div>
                  </AnimatePresence>
                ) : (
                  <div className="flex-1 p-6">
                    <div className="h-full grid place-items-center border border-dashed border-gray-300 rounded-lg">
                      <p className="text-gray-500">
                        Select or create a note to get started
                      </p>
                    </div>
                  </div>
                )}
              </div>
            </div>
          )}

          {activeView === 'kanban' && (
            <KanbanBoard 
              items={kanbanItems} 
              onUpdateItem={updateKanbanItem}
            />
          )}

          {activeView === 'files' && (
            <div className="flex-1 p-6">
              <h2 className="text-xl font-semibold text-gray-900 mb-4">Files</h2>
              <p className="text-gray-500">Files view coming soon...</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
